INSERT INTO `newfeeds` (`newfeed_id`, `newfeed_image`, `newfeed_title`, `newfeed_subtitle`, `number1`, `number2`, `number3`, `image1`, `image2`, `image3`, `content1`, `content2`, `content3`, `created_at`) VALUES
(1, 'https://www.sabeco.com.vn/Data/Sites/1/News/3569/viber_image_2021-08-23_17-56-31-193.jpg', 'SABECO ĐÓNG GÓP 10 TỶ ĐỒNG CHO CÔNG TÁC XÃ HỘI VÀ PHÒNG CHỐNG DỊCH TẠI VIỆT NAM', 'Ngày 19 tháng 8 năm 2021, Tổng công ty Cổ phần Bia – Rượu – Nước giải khát Sài Gòn (SABECO) và Trung ương Hội Liên hiệp Thanh niên Việt Nam chính thức triển khai chương trình “Tiếp sức Việt Nam” với mục tiêu tiếp sức cho các lực lượng tuyến đầu của đất nước, những người đang nỗ lực không ngừng nghỉ để ngăn ngừa đại dich, và san sẻ những khó khăn với các gia đình tại các địa phương đang bị ảnh hưởng nặng nề nhất.', '123 Năm đồng hành', '1200 sản phẩm/tháng', '5 đại lý', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---111.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---8.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---111.jpg', '<div data-wow-delay=\"0\" class=\"intro-content wow fadeInUp\" style=\"visibility: visible; animation-name: fadeInUp;\">\r\n<p style=\"text-align: center;\"><span style=\"font-size: 24px;\"><strong>“Bia Saigon Export – Công thức nguyên bản” </strong></span></p>\r\n<p>Ra đời từ năm 1875, trường tồn cùng thành phố với một hương vị đắm say, luôn đứng vững trong lòng người yêu bia Việt Nam</p>\r\n</div>', '<div data-wow-delay=\"0\" class=\"intro-content wow fadeInLeft\" style=\"visibility: visible; animation-name: fadeInLeft;\">\r\n<p><strong>Chủng loại sản phẩm:</strong> Bia Lager.</p>\r\n<p><strong>Thành phần:</strong> Nước, malt đại mạch, ngũ cốc, hoa bia.</p>\r\n<p><strong>Bao bì: </strong>đóng trong chai thủy tinh màu nâu, 20 chai/két nhựa</p>\r\n<p><strong>Hướng dẫn sử dụng: </strong>Ngon hơn khi uống lạnh, tốt nhất ở 10 – 15 độ C.</p>\r\n<p><strong>Hướng dẫn bảo quản:</strong></p>\r\n<ul>\r\n    <li>Bảo quản nơi khô ráo và thoáng mát.&nbsp;</li>\r\n    <li>Tránh tiếp xúc trực tiếp với nguồn nóng hoặc ánh sáng mặt trời.&nbsp;</li>\r\n</ul>\r\n<p><strong>Thị trường: </strong>Bia chai Saigon Export được sản xuất và phân phối rộng rãi trên cả nước, đặc biệt từ miền Trung trở vào miền Nam.</p>\r\n</div>', '<div data-wow-delay=\"0\" class=\"intro-content wow fadeInLeft\" style=\"visibility: visible; animation-name: fadeInLeft;\">\r\n<p><strong>Chủng loại sản phẩm:</strong> Bia Lager.</p>\r\n<p><strong>Thành phần:</strong> Nước, malt đại mạch, ngũ cốc, hoa bia.</p>\r\n<p><strong>Bao bì: </strong>đóng trong chai thủy tinh màu nâu, 20 chai/két nhựa</p>\r\n<p><strong>Hướng dẫn sử dụng: </strong>Ngon hơn khi uống lạnh, tốt nhất ở 10 – 15 độ C.</p>\r\n<p><strong>Hướng dẫn bảo quản:</strong></p>\r\n<ul>\r\n    <li>Bảo quản nơi khô ráo và thoáng mát.&nbsp;</li>\r\n    <li>Tránh tiếp xúc trực tiếp với nguồn nóng hoặc ánh sáng mặt trời.&nbsp;</li>\r\n</ul>\r\n<p><strong>Thị trường: </strong>Bia chai Saigon Export được sản xuất và phân phối rộng rãi trên cả nước, đặc biệt từ miền Trung trở vào miền Nam.</p>\r\n</div>', '2021-08-30 19:14:47'),
(2, 'https://www.sabeco.com.vn/Data/Sites/1/News/3524/bialacviet-biadichthuccuanguoiviet2cdanhchonguoiviet_1_dbsl.jpg', 'SABECO KỶ NIỆM 145 NĂM – RA MẮT NHÃN HIỆU BIA PHIÊN BẢN ĐẶC BIỆT VÀ CHƯƠNG TRÌNH ', 'chức lễ công bố chuỗi hoạt động nhằm bảo tồn và phát huy các di sản và giá trị cốt lõi của công ty đồng thời hoạch định', '123 Năm đồng hành', '1200 sản phẩm/tháng', '5 đại lý', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---111.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---8.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---111.jpg', '<div data-wow-delay=\"0\" class=\"intro-content wow fadeInUp\" style=\"visibility: visible; animation-name: fadeInUp;\">\r\n<p style=\"text-align: center;\"><span style=\"font-size: 24px;\"><strong>“Bia Saigon Export – Công thức nguyên bản” </strong></span></p>\r\n<p>Ra đời từ năm 1875, trường tồn cùng thành phố với một hương vị đắm say, luôn đứng vững trong lòng người yêu bia Việt Nam</p>\r\n</div>', '<div data-wow-delay=\"0\" class=\"intro-content wow fadeInLeft\" style=\"visibility: visible; animation-name: fadeInLeft;\">\r\n<p><strong>Chủng loại sản phẩm:</strong> Bia Lager.</p>\r\n<p><strong>Thành phần:</strong> Nước, malt đại mạch, ngũ cốc, hoa bia.</p>\r\n<p><strong>Bao bì: </strong>đóng trong chai thủy tinh màu nâu, 20 chai/két nhựa</p>\r\n<p><strong>Hướng dẫn sử dụng: </strong>Ngon hơn khi uống lạnh, tốt nhất ở 10 – 15 độ C.</p>\r\n<p><strong>Hướng dẫn bảo quản:</strong></p>\r\n<ul>\r\n    <li>Bảo quản nơi khô ráo và thoáng mát.&nbsp;</li>\r\n    <li>Tránh tiếp xúc trực tiếp với nguồn nóng hoặc ánh sáng mặt trời.&nbsp;</li>\r\n</ul>\r\n<p><strong>Thị trường: </strong>Bia chai Saigon Export được sản xuất và phân phối rộng rãi trên cả nước, đặc biệt từ miền Trung trở vào miền Nam.</p>\r\n</div>', '<div data-wow-delay=\"0\" class=\"intro-content wow fadeInLeft\" style=\"visibility: visible; animation-name: fadeInLeft;\">\r\n<p><strong>Chủng loại sản phẩm:</strong> Bia Lager.</p>\r\n<p><stro