INSERT INTO `votes` (`vote_id`, `vote_title`, `vote_subtitle`, `vote_image`, `created_at`, `updated_at`) VALUES
(1, 'NGUYỄN THÀNH CÔNG', 'Phát triển SABECO thành Tập đoàn công nghiệp đồ uống hàng đầu của Việt Nam, có vị thế trong khu vực và quốc tế.', 'https://www.paratime.vn/wp-content/uploads/2019/09/timestudio.vn-corporate-headshot-pricing-01.jpg', '2021-08-12', '2021-08-17'),
(2, 'Trần Mạnh Nam', 'Đề cao văn hóa ẩm thực của người Việt Nam.Nâng cao chất lượng cuộc sống thông qua việc cung cấp các sản phẩm đồ uống chất lượng cao, an toàn và bổ dưỡng.\r\nMang lại lợi ích thiết thực cho c', 'https://s1.storage.5giay.vn/image/2020/11/20201127_6da293ecfea1e5d9d1b40cd29f51d51a_1606438789.jpg', '2021-08-11', '2021-08-08'),
(3, 'Lê Thị Tuyết', 'Thỏa mãn và đáp ứng nhu cầu đồ uống theo chuẩn mực an toàn vệ sinh thực phẩm quốc tế “An toàn vệ sinh thực phẩm và bảo vệ môi trường”.', 'https://photo-cms-sggp.zadn.vn/w840/Uploaded/2021/ogidhnatgebat/2021_05_04/chutichkienlong_pqdk.jpg', '2021-08-03', '2021-08-02'),
(4, 'Nguyễn Công Chí', 'Trách nhiệm xã hội: Sự phát triển gắn với trách nhiệm xã hội là truyền thống của SABECO. Chúng tôi cung cấp cho xã hội những sản phẩm an toàn và hữu ích, bên cạnh đó chúng tôi luôn mong muốn cùng chia sẻ và gánh vác những trách nhiệm trong công tác xã hội và bảo vệ môi trường bằng những hành động thiết thực.', 'https://bizweb.dktcdn.net/100/202/714/files/3.jpg', '2021-08-16', '2021-08-08'),
(5, 'Hồ Văn Luận', 'Cải tiến không ngừng: Chúng tôi không thỏa mãn với những gì đang có mà luôn mơ ước vươn lên, học tập, sáng tạo, và đổi mới để đáp ứng ngày càng tốt hơn những nhu cầu ngày càng cao và liên tục thay đổi của thị trường. Thường xuyên học tập, sáng tạo và đổi mới là phong cách của chúng tôi.', 'https://amiedecharme.com/image/603f552c6f70ab16a1417785/original.jpg', '2021-08-03', '2021-08-11'),
(6, 'Trần Thị Mai Thủy', 'Gắn bó: Đề cao sự gắn bó trong một môi trường làm việc thân thiện, chia sẻ. Nơi mà mọi người được tạo mọi điều kiện để học tập, sáng tạo và cống hiến để cùng hưởng niềm vui của thành công', 'https://chupgiare.com/wp-content/uploads/2019/08/kieu-chup-hinh-profile-cong-ty-2.jpg', '2021-08-10', '2021-08-23'),
(7, 'Koh Poh Tiong', 'Châu Đại Dương: Úc, New Zealand, KiribatiChâu Phi: Equatorial Guinea, Gabon, Cameroon, Ghana, Cộng hòa Congo, Benin, Liberia và Sierra Leone.', 'https://www.sabeco.com.vn/Data/Sites/1/News/235/mr-koh-poh-tiong3.jpg', '2021-08-26', '2021-08-16'),
(8, 'Trần Kim Chi', 'Bia Sài Gòn đã được xuất khẩu sang 38 quốc gia:Châu Âu: Pháp, Hà Lan, Anh, Thụy Điển, Thụy Sĩ, Nga, Đan Mạch, Tây Ban Nha, Cộng hòa Séc, Áo, Ý, Đức, RomaniaChâu Á: Lào, Trung Quốc, Đài Loan, Nhật Bản, Hồng Kông, Hàn Quốc , Thái Lan, Singapore, Bahrain, Israel;', 'https://www.sabeco.com.vn/Data/Sites/1/News/1400/ms-nga.jpg', '2021-08-11', '2021-08-02');

